#addin nuget:?package=Cake.Kudu.Client
string baseUri = EnvironmentVariable("KUDU_CLIENT_BASEURI"), userName = EnvironmentVariable("KUDU_CLIENT_USERNAME"), password = EnvironmentVariable("KUDU_CLIENT_PASSWORD");
IKuduClient kuduClient = KuduClient( baseUri, userName, password);
IKuduCommandResult commandResult = kuduClient.ExecuteCommand( "echo", "D:/home/site/wwwroot", "hello");
Information( "Output:\r\n{0}\r\nError:\r\n{1}\r\nExitCode: {2}", commandResult.Output, commandResult.Error, commandResult.ExitCode);

#addin nuget:?package=Cake.Kudu.Client

string  baseUri     = EnvironmentVariable("KUDU_CLIENT_BASEURI"),
        userName    = EnvironmentVariable("KUDU_CLIENT_USERNAME"),
        password    = EnvironmentVariable("KUDU_CLIENT_PASSWORD");

IKuduClient kuduClient = KuduClient(
    baseUri,
    userName,
    password);

ReadOnlyDictionary<string, string> settings = kuduClient.SettingsGet();

foreach(var setting in settings)
{
    Information(
        "{0}={1}",
        setting.Key,
        setting.Value);
}

